create table users (create_at datetime(6), email varchar(255) not null, password varchar(255) not null, userid varchar(255) not null, username varchar(255) not null, primary key (userid));
create table schedule (create_at datetime(6), id bigint not null auto_increment, modified_at datetime(6), contents longtext, title varchar(255) not null, userid varchar(255) not null, primary key (id),FOREIGN KEY (userid) REFERENCES users(userid) ON UPDATE CASCADE ON DELETE CASCADE);
create table comment (create_at datetime(6), id bigint not null auto_increment, modified_at datetime(6), scheduleid bigint not null, comment varchar(255), userid varchar(255) not null, primary key (id),FOREIGN KEY (scheduleid) REFERENCES schedule(id) ON UPDATE CASCADE ON DELETE CASCADE,FOREIGN KEY (userid) REFERENCES user(userid) ON UPDATE CASCADE ON DELETE CASCADE);
